import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../../componets/Navbar';
import PropertyMap from '../../componets/PropertyMap';
import Search from '../../componets/Search';
// import { Container } from './styles';
import api from '../../services/api';
export default class Property extends Component{
    state = {
      estate: [],
    }
  componentDidMount() {

    api.get(`/show/id/${this.props.match.params.id}`)
    .then(res => {
      const estate = res.data;
      this.setState({ estate });
    })
  }
  render(){
    return (
      <Fragment>

        <Navbar></Navbar>
        <Search></Search>




      { this.state.estate.map(estate => {
          return(
          <section className="property-single-pg">
            <div className="container">
            <div className="property-hd-sec">
                    <div className="card">
                        <div className="card-body">
                            <Link to="/">
    <h3>{estate.name}</h3>
                                <p><i className="la la-map-marker"></i>212 5th Ave, New York</p>
                            </Link>
                            { estate.detail.map(item => {
                              return(
                            <ul>

                                <li>{item.bath} banheiros</li>
                                <li>{item.beds} quartos</li>
                                <li>{item.rooms} salas</li>



                            </ul>
                            )
                            })}
                        </div>
                        <div className="rate-info">
                            <h5>R${estate.price}</h5>
                            <span>{estate.estate_type}</span>
                        </div>
                    </div>
                </div>

            <div className="property-single-page-content">
              <div className="row">
              <div className="col-lg-8 pl-0 pr-0">
                <div className="property-pg-left">
                {/* Descricao */}
                <div className="descp-text">
                  <h3>Descrição</h3>
                  <p>{estate.description}</p>
                </div>
                {/* DESCRICAO FECHA */}
                <div class="details-info">
                   <h3>Detalhes</h3>
                   <ul>
                     {estate.detail.map(item=>{
                       return(
                        <li>
                          <h4>Quartos:</h4>
                          <span > {item.beds}</span>
                        </li>
                       )
                     })}

                   </ul>
                  </div>

                  {/* FEATURES */}
                  <div class="features-dv">
                    <h3>Features</h3>
                    <form class="form_field">
                        <ul>
                        {estate.features.map(item=>{
                    return(  
                        <li class="input-field">
                                <input type="checkbox" name="cc" id="c2" checked/>
                                <label for="c2">
                                    <span></span>
                                    <small>{item}</small>
                                </label>
                            </li>
                            )
                          })}

                        </ul>
                    </form>
                </div>

                  {/* MAP */}
                  {estate.location.map(item=>{
                       return(
                        <PropertyMap lat={item.lat} lng={item.lng}></PropertyMap>
                       )
                     })}

                </div>
              </div>

              <div className="col-lg-4 pr-0">
                            <div class="sidebar layout2">
                                <div className="widget widget-form">
                                    <h3 className="widget-title">Contact Listing Agent</h3>
                                    <div className="contct-info">
                                        <img src="https://via.placeholder.com/81x74" alt=""/>
                                        <div className="contct-nf">
                                            <h3>Tomas Wilkinson</h3>
                                            <h4>Douglas and Eleman Agency</h4>
                                            <span><i class="la la-phone"></i>+1 212-925-3797</span>
                                        </div>
                                    </div>
                                    <div className="post-comment-sec">
                                        <form>
                                            <div className="form-field">
                                                <input type="text" name="name" placeholder="Your Name"/>
                                            </div>
                                            <div className="form-field">
                                                <input type="text" name="email" placeholder="Your Email"/>
                                            </div>
                                            <div className="form-field">
                                                <input type="text" name="phone" placeholder="Your Phone"/>
                                            </div>
                                            <div className="form-field">
                                                <textarea name="message" placeholder="Your Message"></textarea>
                                            </div>
                                            <button type="submit" class="btn2">Contact</button>
                                        </form>
                                    </div>
                                </div>

                            </div>
                        </div>

              </div>
            </div>

            </div>

                </section>
                )})}
      </Fragment>

    );
  }
}
